import './App.css';
import React, { Component } from 'react';
import axios from 'axios';

class Users extends Component {
  constructor() {
    super();
    this.state = {
      users: [],
      filteredUsers: [],
      loading: true,
      nameFilter: '',
      descriptionFilter: '',
    };
  }

  componentDidMount() {
    this.getUsers();
  }

  getUsers() {
    axios.get(`http://localhost:8000/api/users`).then((response) => {
      this.setState({
        users: response.data,
        filteredUsers: response.data,
        loading: false,
      });
    });
  }

  handleNameFilterChange = (event) => {
    const nameFilter = event.target.value;
    this.setState({ nameFilter }, this.filterUsers);
  };

  handleDescriptionFilterChange = (event) => {
    const descriptionFilter = event.target.value;
    this.setState({ descriptionFilter }, this.filterUsers);
  };

  filterUsers() {
    const { users, nameFilter, descriptionFilter } = this.state;
    const filteredUsers = users.filter((user) =>
      user.name.toLowerCase().includes(nameFilter.toLowerCase()) &&
      user.description.toLowerCase().includes(descriptionFilter.toLowerCase())
    );
    this.setState({ filteredUsers });
  }

  handleSearchClick = () => {
    // You can call your API here with the filters applied
    // You may want to implement pagination and send filter parameters to the API
    // Example: axios.get('http://localhost:8000/api/users', { params: { name: this.state.nameFilter, description: this.state.descriptionFilter } })
    // Update the state with the new data
  }

  render() {
    const loading = this.state.loading;
    return (
      <div>
        <section className="row-section">
          <div className="container">
            <div className="row">
              <h2 className="text-center">
                <span>List of users</span> Created with{' '}
                <i className="fa fa-heart"></i> by yemiwebby
              </h2>
            </div>
            <div className="filters">
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Filter by name"
                  value={this.state.nameFilter}
                  onChange={this.handleNameFilterChange}
                />
              </div>
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Filter by description"
                  value={this.state.descriptionFilter}
                  onChange={this.handleDescriptionFilterChange}
                />
              </div>
              <button
                className="btn btn-primary"
                onClick={this.handleSearchClick}
              >
                Search
              </button>
            </div>
            {loading ? (
              <div className={'row text-center'}>
                <span className="fa fa-spin fa-spinner fa-4x"></span>
              </div>
            ) : (
              <div className={'row'}>
                {this.state.filteredUsers.map((user) => (
                  <div
                    className="col-md-10 offset-md-1 row-block"
                    key={user.id}
                  >
                    <ul id="sortable">
                      <li>
                        {/* Render user data here */}
                      </li>
                    </ul>
                  </div>
                ))}
              </div>
            )}
          </div>
        </section>
      </div>
    );
  }
}

export default Users;
