import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

class Users extends Component {
  constructor() {
    super();
    this.state = { users: [], loading: true, filterFrequency: '', filterDropdown: '' };
  }

  componentDidMount() {
    this.getUsers();
  }

  getUsers() {
    axios.get(`http://localhost:8000/api/users`).then(users => {
      this.setState({ users: users.data, loading: false });
    });
  }

  handleFilterChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  render() {
    const { loading, filterFrequency, filterDropdown } = this.state;
    const filteredUsers = this.state.users.filter(user => {
      return user.frequency.includes(filterFrequency) && user.dropdown === filterDropdown;
    });

    return (
      <div className="row">
        <div className="col-md-3">
          <div className="filter-sidebar">
            <h3>Filter</h3>
            <label>Frequency:</label>
            <input
              type="text"
              name="filterFrequency"
              value={filterFrequency}
              onChange={this.handleFilterChange}
            />
            <label>Dropdown:</label>
            <select
              name="filterDropdown"
              value={filterDropdown}
              onChange={this.handleFilterChange}
            >
              <option value="">All</option>
              <option value="Option1">Option 1</option>
              <option value="Option2">Option 2</option>
              {/* Add more options as needed */}
            </select>
          </div>
        </div>
        <div className="col-md-9">
          <section className="row-section">
            <div className="container">
              <div className="row">
                <h2 className="text-center">
                  <span>List of users</span> Created with{' '}
                  <i className="fa fa-heart"></i> by yemiwebby
                </h2>
              </div>
              {loading ? (
                <div className="row text-center">
                  <span className="fa fa-spin fa-spinner fa-4x"></span>
                </div>
              ) : (
                <div className="row">
                  {filteredUsers.map(user => (
                    <div className="col-md-10 offset-md-1 row-block" key={user.id}>
                      <ul id="sortable">
                        <li>
                          <div className="media">
                            <div className="media-left align-self-center">
                              <img className="rounded-circle" src={user.imageURL} alt="sd" />
                            </div>
                            <div className="media-body">
                              <h4>{user.name}</h4>
                              <p>{user.description}</p>
                            </div>
                            <div className="media-right align-self-center">
                              <a href="http://contact.com" className="btn btn-default">
                                Contact Now
                              </a>
                            </div>
                          </div>
                        </li>
                      </ul>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </section>
        </div>
      </div>
    );
  }
}

export default Users;
