import React, { Component } from 'react';

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchTerm: '',
      ageRange: 30, // Default age range value
      selectedOption: 'all', // Default filter option
      showFilters: false, // Controls whether the filters are shown
    };
  }

  handleSearchInputChange = (e) => {
    this.setState({ searchTerm: e.target.value });
    // You can add code here to filter the user list based on the search term.
  };

  handleAgeRangeChange = (e) => {
    this.setState({ ageRange: e.target.value });
    // You can add code here to filter the user list based on the age range.
  };

  handleFilterChange = (e) => {
    this.setState({ selectedOption: e.target.value });
    // You can add code here to filter the user list based on the selected filter option.
  };

  toggleFilters = () => {
    this.setState((prevState) => ({
      showFilters: !prevState.showFilters,
    }));
  };

  render() {
    return (
      <div className="search-container">
        <h2>Search</h2>
        <input
          type="text"
          placeholder="Search users"
          value={this.state.searchTerm}
          onChange={this.handleSearchInputChange}
        />
        <button onClick={this.toggleFilters}>
          {this.state.showFilters ? 'Hide Filters' : 'Show Filters'}
        </button>
        {this.state.showFilters && (
          <div className="filters-drawer">
            <h3>Age Range: {this.state.ageRange}</h3>
            <input
              type="range"
              min="0"
              max="100"
              step="1"
              value={this.state.ageRange}
              onChange={this.handleAgeRangeChange}
            />
            <h3>Filter by:</h3>
            <select
              value={this.state.selectedOption}
              onChange={this.handleFilterChange}
            >
              <option value="all">All</option>
              <option value="option1">Option 1</option>
              <option value="option2">Option 2</option>
              <option value="option3">Option 3</option>
              {/* Add more filter options as needed */}
            </select>
          </div>
        )}
        {/* Add your search button or functionality here */}
      </div>
    );
  }
}

export default Search;
